import React from "react";
// import { useLocation } from "react-router-dom";
import Logo from "../Prepifi.png";

function MockInterview() {
  //   const location = useLocation();
  //   const { formData } = location.state || {};

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      {/* Logo */}
      <img src={Logo} alt="Prepifi Logo" className="w-40 mb-4" />

      {/* Main Interview Container */}
      <div className="flex flex-row justify-evenly w-[80%] bg-white shadow-md rounded-lg p-8">
        {/* Interviewer Video Section */}
        <div className="flex flex-col items-center justify-center w-[45%] h-[300px] bg-gray-300 rounded-lg relative">
          <img
            src="https://imgcdn.stablediffusionweb.com/2024/10/5/b787d704-676a-4116-969f-71cedf2e92e8.jpg"
            alt="Interviewer Avatar"
            className="absolute bottom-24 w-32 h-32 rounded-full border-white"
          />
          {/* <img
            src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Red-Circle-Transparent.png" // Replace with your actual interviewer avatar URL
            alt="Interviewer Avatar"
            className="absolute top-4 right-4 w-3 h-3 rounded-full border-white animate-pulse animate-fast"
          /> */}
          <span className="absolute bottom-16 text-lg text-center font-semibold rounded-full">
            Onyx AI
          </span>
        </div>

        {/* Candidate Video Section */}
        <div className="flex items-center justify-center w-[45%] h-[300px] bg-gray-300 rounded-lg">
          <video
            id="candidateVideo"
            className="w-full h-full object-cover rounded-lg"
            autoPlay
            muted
          ></video>
        </div>

        <script>
          {`
            navigator.mediaDevices.getUserMedia({ video: true })
              .then(stream => {
            const video = document.getElementById('candidateVideo');
            if (video) {
              video.srcObject = stream;
            }
              })
              .catch(error => {
            console.error('Error accessing user camera:', error);
              });
          `}
        </script>
      </div>

      {/* Transcript Section */}
      <div className="w-[80%] mt-6">
        <h3 className="text-lg font-semibold mb-2">Transcript:</h3>
        <p className="bg-white p-4 shadow rounded-lg">
          Hello! I am the hiring manager. Let's start the interview. Can you
          please tell me a bit about yourself?
        </p>
      </div>

      {/* Form Data Section */}
      {/* <div className="w-[80%] mt-6">
        <h3 className="text-lg font-semibold mb-2">Form Data:</h3>
        <div className="bg-white p-4 shadow rounded-lg">
          <pre>{JSON.stringify(formData, null, 2)}</pre>
        </div>
      </div> */}

      {/* End Interview Button */}
      <button
        className="mt-6 bg-black text-white px-6 py-2 rounded-full hover:bg-gray-800"
        onClick={() => console.log("End Interview")}
      >
        End Interview
      </button>
    </div>
  );
}

export default MockInterview;
