import React from "react";
import { useLocation } from "react-router-dom";
import Logo from "../Prepifi.png";

function MockInterview() {
  const location = useLocation();

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      {/* Logo */}
      <img src={Logo} alt="Prepifi Logo" className="w-40 mb-4" />

      {/* Main Interview Container */}
      <div className="flex flex-row justify-between w-[80%] bg-white shadow-md rounded-lg p-8">
        {/* Interviewer Video Section */}
        <div className="flex flex-col items-center justify-center w-[45%] h-[300px] bg-gray-300 rounded-lg relative">
          <img
            src="https://via.placeholder.com/50" // Replace with your actual interviewer avatar URL
            alt="Interviewer Avatar"
            className="absolute bottom-2 left-2 w-12 h-12 rounded-full border-2 border-white"
          />
        </div>

        {/* Candidate Video Section */}
        <div className="flex items-center justify-center w-[45%] h-[300px] bg-gray-300 rounded-lg">
          <img
            src="https://via.placeholder.com/150" // Replace with your actual candidate placeholder
            alt="Candidate Placeholder"
            className="w-24 h-24"
          />
        </div>
      </div>

      {/* Transcript Section */}
      <div className="w-[80%] mt-6">
        <h3 className="text-lg font-semibold mb-2">Transcript:</h3>
        <p className="bg-white p-4 shadow rounded-lg">
          Hello! I am the hiring manager. Let's start the interview. Can you
          please tell me a bit about yourself?
        </p>
      </div>

      {/* End Interview Button */}
      <button
        className="mt-6 bg-black text-white px-6 py-2 rounded-full hover:bg-gray-800"
        onClick={() => console.log("End Interview")}
      >
        End Interview
      </button>
    </div>
  );
}

export default MockInterview;
