import React, { useState } from "react";
import axios from "axios";

const Chat: React.FC = () => {
  const [input, setInput] = useState("");
  const [logs, setLogs] = useState<string[]>([]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Add user input to chat logs
    setLogs((prev) => [...prev, `User: ${input}`]);

    try {
      // Send input to the /hello API
      const response = await axios.get("/api/hello");
      setLogs((prev) => [...prev, `Bot: ${response.data.response}`]);
    } catch (error) {
      setLogs((prev) => [...prev, "Bot: Error fetching response"]);
    }

    setInput(""); // Clear input
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold">AI Interview Trainer</h1>
      <form onSubmit={handleSubmit} className="mt-4">
        <textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          className="border p-2 w-full"
          placeholder="Type your question..."
        ></textarea>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 mt-2">
          Send
        </button>
      </form>
      <div className="mt-4 bg-white p-4 rounded shadow">
        {logs.map((log, index) => (
          <p key={index}>{log}</p>
        ))}
      </div>
    </div>
  );
};

export default Chat;
