import React, { useState } from "react";
import Logo from "../Prepifi.png";

function Onboard() {
  const [formData, setFormData] = useState({
    fullName: "",
    pronouns: "",
    age: "",
    title: "",
    company: "",
    jobDescription: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = () => {
    // You can handle the form submission here
    console.log(formData);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen">
      <img src={Logo} alt="" />
      <div className="w-[30%]">
        <h2 className="font-black mt-1 text-center">PERSONAL INFO</h2>
        <input
          type="text"
          name="fullName"
          placeholder="Full Name"
          value={formData.fullName}
          onChange={handleChange}
          className="mt-4 p-2 border w-[100%] border-gray-300 rounded placeholder-gray-400"
        />
        <div className="flex justify-between gap-2">
          <select
            name="pronouns"
            value={formData.pronouns}
            onChange={handleChange}
            className="mt-4 p-2 border w-[100%] border-gray-300 rounded text-gray-400 placeholder-gray-400"
          >
            <option className="text-gray-400" value="" disabled>
              Select Pronouns
            </option>
            <option value="he/him">He/Him</option>
            <option value="she/her">She/Her</option>
            <option value="they/them">They/Them</option>
            <option value="other">Other</option>
          </select>
          <input
            type="number"
            name="age"
            placeholder="Age"
            value={formData.age}
            onChange={handleChange}
            className="mt-4 p-2 border border-gray-300 rounded placeholder-gray-400"
          />
        </div>
      </div>

      <div className="w-[30%]">
        <h2 className="font-black mt-10 text-center">JOB INFO</h2>
        <div className="flex justify-between gap-2">
          <input
            type="text"
            name="title"
            placeholder="Title"
            value={formData.title}
            onChange={handleChange}
            className="mt-4 p-2 border w-[100%] border-gray-300 rounded placeholder-gray-400"
          />
          <input
            type="text"
            name="company"
            placeholder="Company"
            value={formData.company}
            onChange={handleChange}
            className="mt-4 p-2 border w-[100%] border-gray-300 rounded placeholder-gray-400"
          />
        </div>
        <textarea
          name="jobDescription"
          placeholder="Job Description"
          value={formData.jobDescription}
          onChange={handleChange}
          className="mt-4 p-2 border w-[100%] border-gray-300 rounded placeholder-gray-400"
          rows={4}
        />
      </div>

      <button
        onClick={handleSubmit}
        className="mt-6 w-[10%] p-2 bg-[#5379ce] text-white rounded-full hover:bg-blue-600"
      >
        Submit
      </button>

      <DisplayData formData={formData} />
    </div>
  );
}

function DisplayData({ formData }: { formData: any }) {
  return (
    <div>
      <h3>Form Data:</h3>
      <pre>{JSON.stringify(formData, null, 2)}</pre>
    </div>
  );
}

export default Onboard;
